import { useEffect, useState, useRef, Dimensions, Image, StyleSheet, View  } from 'react';
import { motion, useAnimation, useMotionValue, useTransform } from "framer-motion";
import { useInView } from "react-intersection-observer" 
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import AOS from "aos";
import "aos/dist/aos.css"
import {fadeIn} from "react-fade-in"
import "../../src/style.css"
import {useSpring, animated as a} from "react-spring"
import {Carousel} from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import Swiper from 'swiper';
import { useMediaQuery } from 'react-responsive';
import "../../node_modules/swiper/swiper.css";
import "../../node_modules/swiper/swiper.min.css";
import work from "../Components/SpringIn/Work/Coisas/work.json"
import fromWork from "../Components/SpringIn/Work/Images/fromWork.jpeg"
import imagem1 from "./Imagens/Imagem1.jpeg"
import imagem2 from "./Imagens/Imagem2.jpeg"
import imagem3 from "./Imagens/Imagem3.jpeg"


export default function Work(){
    // hamburguer
  const [active, setActive] = useState(false); 
  const toggleMenu = () => { 
    setActive(!active);
  };

  // Cursor effect
  const [mousePosition, setMousePosition] = useState({
    x: 0,
    y: 0
  })
  const [cursorVariant, setCursorVariant] = useState("default");
  
  useEffect(() =>{
    const mouseMove = e => {
      setMousePosition({
        x: e.clientX,
        y: e.clientY
      })
      
    }
    

    window.addEventListener("mousemove", mouseMove)

    
    return () => {
      window.removeEventListener("mousemove", mouseMove)
    }
  }, []); // executado uma vez devido à array vazia
  
  const variants = {
    default: {
      x: mousePosition.x - 16,
      y: mousePosition.y - 16  
    },
    container: {
      
      backgroundColor: "orange",
      x: mousePosition.x - 16,
      y: mousePosition.y - 16
    },

    
  }

  const containerEnter = () => setCursorVariant("container");
  const containerLeave = () => setCursorVariant("default")



  

    // scroll effect


    useEffect(()=>{
      AOS.init({
        duration: 1000, // duração da animação em milissegundos
        offset: 200, // offset (em pixels) em relação ao topo do elemento em que a animação é disparada
        easing: "ease-in-out", // tipo de easing utilizado na animação
        delay: 100 // ttempo (em milissegundos) de atraso antes do início da animação
      });
      
     
    }, [])

    // Pegando coisas do AboutMe.json

    const [especialidades, setEspecialidades] = useState([]);

    useEffect(() => {
        setEspecialidades(work || []);
      }, []);

     // Imagens
     const imagens = [imagem1, imagem2, imagem3] 
     const [images, setImages] = useState([]);

     useEffect(() =>{
      setImages(imagens || [])
     }, [])

    return(
<>
<div className='navBar2'
    onMouseEnter={containerEnter} 
    onMouseLeave={containerLeave}
    >

      <motion.div 
    className="cursor"
    variants={variants}
    animate={cursorVariant}
    />
    <motion.div 
    className='cursor2'
    variants={variants}
    animate={cursorVariant}
    />
     <div className={active ? "icon iconActive" : "icon"} onClick={toggleMenu}>
        <div className="hamburguer hamburguerIcon"></div>
      </div>   
      <div className={active ? "menu menuOpen" : "menu menuClose"}>

        <div className="list">
          <ul className="listItems">
            <li><a href="http://localhost:3000/">HOME</a></li>
            <li><a href="http://localhost:3000/work">WORK</a></li>
            <li><a href="http://localhost:3000/aboutme">ABOUT</a></li>
            <li><a href="http://localhost:3000/contact">CONTACT</a></li>
        </ul>
        <h4>Vamos encontrar soluções juntos?</h4>
        <a className="EntrarContato">Entre em contato para construirmos o seu projeto juntos!</a>
        <a className="email">erickrodovalhosilveira@gmail.com</a>
    
        </div>
      </div>
      <div className="logo">
        <a className>Ers</a>
</div>
<div className="effectPhrase">
    <h1>Conheça os meus trabalhos.</h1>
    <p>De aplicações <span className='color1'>simples</span> das mais <span className='color2'>complexas</span>.</p>
    <p>Trato qualquer projeto com a mesma intensidade de <span className='color2'>esforço</span>.</p>
    <p className='pMaior'>Faço aplicações frontend com mais de um tipo de biblioteca em 
        <span className='color1'>JavaScript</span>. No backend as principais linguagens (bibliotecas) 
        e banco de dados que utilizo são: 
        <span className='color1'> PHP, NodeJS, ExpressJS, MongoDB e SQL.</span>
        </p>
</div>
<div className='photoMework'>
    <img src={fromWork}/>
</div>
</div>
<div className='myServices2'
 data-aos="fade-up"
 data-aos-duration="2000"
 data-aos-once="true"
 data-aos-offset="0"
>
 <div className='sobreEspecialidades'>
 <h1 >Meus trabalhos.</h1>
  </div>
  <div className="especialidades">
        {especialidades && especialidades.map((record) => (
          <div key={record.id} className="especialidade">
            
            <h1 className='espc1'>{record.title}</h1>
            <h2 className='espc2'>{record.subtitle}</h2>
          </div>
        ))}
      {
        images && images.map((item)=>(
          <img className='photoSpace'/>
        ))
      }
      </div>

  </div>
  <div className='workMe2'>
  <h1 className='letsCode'>
    Vamos programar algo <span className='color1'>juntos?</span>
  </h1>
  <button className='contactButton'>
    <a href='#'>Contato</a>
  </button>
  <h1 className='socialMedia'>
    Você pode me achar nas <span className='color2'>redes sociais:</span>
  </h1>
  <a href='https://www.linkedin.com/in/erick-rodovalho-989685257/' className='linkedin'>Linkedin</a>
  <a href='https://github.com/Elrekito1' className='github'>GitHub</a>
  <a href='https://www.instagram.com/erickkrodovalho/' className='instagram'>Instagram</a>
  <a href='https://w.app/work'className='whatsapp'>WhatsApp</a>

  <h4>Copyright © Erick Rodovalho da Silveira. All rights reserved</h4>
    </div>
</>
    )
}
