import { useEffect, useState, useRef, Dimensions, Image, StyleSheet, View  } from 'react';
import { motion, useAnimation, useMotionValue, useTransform } from "framer-motion";
import { useInView } from "react-intersection-observer" 
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import AOS from "aos";
import "aos/dist/aos.css"
import {fadeIn} from "react-fade-in"
import "../../src/style.css"
import {useSpring, animated as a} from "react-spring"
import {Carousel} from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import Swiper from 'swiper';
import { useMediaQuery } from 'react-responsive';
import "../../node_modules/swiper/swiper.css";
import "../../node_modules/swiper/swiper.min.css";
import Imagem1 from "./Imagens/Imagem1.jpeg"
import Imagem2 from "./Imagens/Imagem2.jpeg"
import Imagem3 from "./Imagens/Imagem3.jpeg"
import Video1 from "./Imagens/Video1.mp4"
import Video2 from "./Imagens/Video2.mp4"
import Video3 from "./Imagens/Video3.mp4"
import Video4 from "./Imagens/Video4.mp4"
import Video5 from "./Imagens/Video5.mp4"












function Home() {

 const [active, setActive] = useState(false);

  const [data, setData] = useState([]);
  const carousel = useRef();
  const [currentPage, setCurrentPage] = useState(0);

  useEffect(() => {
    fetch("http://localhost:3000/static/services.json")
      .then((response) => response.json())
      .then((responseData) => {
        setData(responseData);
        const interval = setInterval(() => {
          if (responseData && responseData.length > 0) {
            setCurrentPage((prevPage) => (prevPage + 1) % responseData.length);
          }
        }, 5000); // Intervalo definido para 5 segundos (5000ms)

        return () => clearInterval(interval);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });

    AOS.init({
      duration: 1000,
      offset: 200,
      easing: "ease-in-out",
      delay: 100
    });
  }, []);

  if (!data || !data.length) return null;

  const images = [Imagem1, Imagem2, Imagem3];
  const videos = [Video1, Video2, Video3, Video4, Video5];
 
  //

  // Corrigindo erro fetch
  function Home() {
    const [data, setData] = useState([]);
  
    useEffect(() => {
      const fetchData = async () => {
        try {
          const response = await fetch("http://localhost:3000/static/services.json");
          if (response.ok) {
            const fetchedData = await response.json();
            setData(fetchedData);
          } else {
            throw new Error('Failed to fetch data');
          }
        } catch (error) {
          console.error('Error fetching data:', error);
          // Aqui você pode decidir como lidar com o erro, seja definindo um estado de erro,
          // exibindo uma mensagem para o usuário ou qualquer outra ação apropriada.
        }
      };
  
      fetchData();
    }, []); // O array vazio garante que o useEffect só seja executado uma vez após a montagem inicial
  
}



  return (
    <>
    <div className='navBar'
    onMouseEnter={containerEnter} 
    onMouseLeave={containerLeave}
    >
    <motion.div 
    className="cursor"
    variants={variants}
    animate={cursorVariant}
    />
    <motion.div 
    className='cursor2'
    variants={variants}
    animate={cursorVariant}
    />
      <div className={active ? "icon iconActive" : "icon"} onClick={toggleMenu}>
        <div className="hamburguer hamburguerIcon"></div>
      </div>
      <div className={active ? "menu menuOpen" : "menu menuClose"}>

        <div className="list">
          <ul className="listItems">
            <li><a href="http://localhost:3000/">HOME</a></li>
            <li><a href="http://localhost:3000/work">WORK</a></li>
            <li><a href="http://localhost:3000/aboutme">ABOUT</a></li>
            <li><a href="http://localhost:3000/contact">CONTACT</a></li>
        </ul>
        <h4>Vamos encontrar soluções juntos?</h4>
        <a className="EntrarContato">Entre em contato para construirmos o seu projeto juntos!</a>
        <a className="email">erickrodovalhosilveira@gmail.com</a>
    
        </div>
      </div>
      <div className="logo">
        <a className>Ers</a>
</div>
