import { useEffect, useState, useRef, Dimensions, Image, StyleSheet, View  } from 'react';
import { motion} from "framer-motion";

import AOS from "aos";
import "aos/dist/aos.css"
import "../../src/style.css"
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import "../../node_modules/swiper/swiper.css";
import "../../node_modules/swiper/swiper.min.css";
import espcs from "../Components/SpringIn/AboutMe/Coisas/AboutMe.json"


export default function Contact(){
 // Enviar formulário
 const [formData, setFormData] = useState({
  name: '',
  email: '',
  options: '',
  time: '',
  description: ''
});

const handleSubmit = async (e) => {
  e.preventDefault();
  try {
    const response = await fetch('http://localhost:8000/enviar-email', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(formData)
    });

    if (response.ok) {
      console.log('Dados enviados com sucesso!');
      // Limpar os campos do formulário após o envio bem-sucedido
      setFormData({
        name: '',
        email: '',
        options: '',
        time: '',
        description: ''
      });
    } else {
      console.error('Erro ao enviar dados');
    }
  } catch (error) {
    console.error('Erro ao enviar dados', error);
  }
};

const handleChange = (e) => {
  const { name, value } = e.target;
  setFormData({
    ...formData,
    [name]: value
  });
};
  
  // hamburguer
  const [active, setActive] = useState(false); 
  const toggleMenu = () => { 
    setActive(!active);
  };

  // Cursor effect
  const [mousePosition, setMousePosition] = useState({
    x: 0,
    y: 0
  })
  const [cursorVariant, setCursorVariant] = useState("default");
  
  useEffect(() =>{
    const mouseMove = e => {
      setMousePosition({
        x: e.clientX,
        y: e.clientY
      })
      
    }
    

    window.addEventListener("mousemove", mouseMove)

    
    return () => {
      window.removeEventListener("mousemove", mouseMove)
    }
  }, []); // executado uma vez devido à array vazia
  
  const variants = {
    default: {
      x: mousePosition.x - 16,
      y: mousePosition.y - 16  
    },
    container: {
      
      backgroundColor: "orange",
      x: mousePosition.x - 16,
      y: mousePosition.y - 16
    },

    
  }

  const containerEnter = () => setCursorVariant("container");
  const containerLeave = () => setCursorVariant("default")



  

    // scroll effect


    useEffect(()=>{
      AOS.init({
        duration: 1000, // duração da animação em milissegundos
        offset: 200, // offset (em pixels) em relação ao topo do elemento em que a animação é disparada
        easing: "ease-in-out", // tipo de easing utilizado na animação
        delay: 100 // ttempo (em milissegundos) de atraso antes do início da animação
      });
      
     
    }, [])

    // Pegando coisas do AboutMe.json

    const [especialidades, setEspecialidades] = useState([]);

    useEffect(() => {
        setEspecialidades(espcs || []);
      }, []);

// Manipulando formulário


    return(
        <>
         <div className='navBar2'
    onMouseEnter={containerEnter} 
    onMouseLeave={containerLeave}
    >

      <motion.div 
    className="cursor"
    variants={variants}
    animate={cursorVariant}
    />
    <motion.div 
    className='cursor2'
    variants={variants}
    animate={cursorVariant}
    />
     <div className={active ? "icon iconActive" : "icon"} onClick={toggleMenu}>
        <div className="hamburguer hamburguerIcon"></div>
      </div>   
      <div className={active ? "menu menuOpen" : "menu menuClose"}>

        <div className="list">
          <ul className="listItems">
            <li><a href="http://localhost:3000/">HOME</a></li>
            <li><a href="http://localhost:3000/work">WORK</a></li>
            <li><a href="http://localhost:3000/aboutme">ABOUT</a></li>
            <li><a href="http://localhost:3000/contact">CONTACT</a></li>
        </ul>
        <h4>Vamos encontrar soluções juntos?</h4>
        <a className="EntrarContato">Entre em contato para construirmos o seu projeto juntos!</a>
        <a className="email">erickrodovalhosilveira@gmail.com</a>
    
        </div>
      </div>
      <div className="logo">
        <a className>Ers</a>
</div>
<div className="effectPhrase">
    <h1>Vamos trabalhar juntos?</h1>
    <p>Preencha o formulário
         e coloque todas as suas demandas!</p>
</div>
<div className="form">
        <form onSubmit={handleSubmit}>
          <div className='formulario1'>
            <label htmlFor="name" className='nome'>Nome</label>
            <input
              type='text'
              id='name'
              name='name'
              className='textArea'
              placeholder='Digite seu nome'
              value={formData.name}
              onChange={handleChange}
            />
          </div>
          <div className='formulario2'>
            <label htmlFor="email" className='email2'>Email</label>
            <input
              type='text'
              id='email'
              name='email'
              className='textArea'
              placeholder='Digite seu email'
              value={formData.email}
              onChange={handleChange}
            />
          </div>
          {/* Campos restantes do formulário adaptados conforme a estrutura existente */}
          {/* ... */}
          <div className='formulario3'>
            <label htmlFor="description" className='detalhes'>Me dê mais detalhes</label>
            <textarea
              type='text'
              id='description'
              name='description'
              className='detailsArea'
              placeholder='O que te deixaria satisfeito no projeto?'
              value={formData.description}
              onChange={handleChange}
            />
          </div>
          <button type='submit' className='botao'>Enviar</button>
        </form>
      </div>
</div>
<div className='workMe2'>
  <h1 className='letsCode'>
    Vamos programar algo <span className='color1'>juntos?</span>
  </h1>
  <button className='contactButton'>
    <a href='#'>Contato</a>
  </button>
  <h1 className='socialMedia'>
    Você pode me achar nas <span className='color2'>redes sociais:</span>
  </h1>
  <a href='https://www.linkedin.com/in/erick-rodovalho-989685257/' className='linkedin'>Linkedin</a>
  <a href='https://github.com/Elrekito1' className='github'>GitHub</a>
  <a href='https://www.instagram.com/erickkrodovalho/' className='instagram'>Instagram</a>
  <a href='https://w.app/work'className='whatsapp'>WhatsApp</a>

  <h4>Copyright © Erick Rodovalho da Silveira. All rights reserved</h4>
    </div>
    
        </>
    )
}
